window.addEventListener("load", function() {
    const a = document.createElement("a");
    a.style = "background-color: #fff59c; color: #000000; display: block; margin: -20px -30px 20px -30px; padding: 30px 30px 20px 30px; text-align: center";
    a.innerText = "The documentation has been moved to https://leodeveloper.pages.dev.";
    a.href = "https://leodeveloper.pages.dev";
    document.body.insertBefore(a, document.body.firstChild);
})